(function(a){a.create("tinymce.plugins.EmojiPlugin",{init:function(b,c){b.addCommand("mceEmoji",function(){b.windowManager.open({file:c+"/emoji.htm",width:480+parseInt(b.getLang("emoji.delta_width",0)),height:400+parseInt(b.getLang("emoji.delta_height",0)),inline:1},{plugin_url:c})});b.addButton("emoji",{title:"絵文字の挿入",cmd:"mceEmoji",image:c+"/img/button.gif"})},getInfo:function(){return{longname:"Emoji",author:"Alfasado Inc.",authorurl:"http://alfasado.net/",infourl:"http://powercms.alfasado.net/",version:a.majorVersion+"."+a.minorVersion}}});a.PluginManager.add("emoji",a.plugins.EmojiPlugin)})(tinymce);